package com.jancar.fragment;import com.jancar.ui.utils.ToastUtil;import com.jancar.uibase.LoadingDialog;import com.jancar.uibase.PublicDialog;import com.jancar.uibase.R;import com.jancar.viewbase.dialog.AbsLoadingDialog;import com.jancar.viewbase.dialog.AbsPublicDialog;import com.jancar.viewbase.fragment.ViewBaseFragmentActivity;import com.jancar.viewbase.utils.AbsToast;/** * BaseFragmentActivity，所有FragmentActivity基类 * <p> * 修改备注：增加接口，在子类中可以得到当前是否是已经打开activity的状态 * 增加获取资源文件接口，getResId 通过资源文件名获取资源文件id */public abstract class BaseFragmentActivity extends ViewBaseFragmentActivity {    @Override    protected AbsToast getToast() {        return ToastUtil.getInstance();    }    @Override    protected AbsLoadingDialog getLoadingDialog() {        return new LoadingDialog(this);    }    @Override    protected AbsPublicDialog getPublicDialog() {        return new PublicDialog(this);    }    @Override    protected void startActivityWithTransition() {        overridePendingTransition(R.anim.push_right_in, R.anim.none);    }    @Override    protected void closeActivityWithTransition() {        overridePendingTransition(R.anim.none, R.anim.push_right_out);    }    @Override    protected String getLoadingText() {        return getString(R.string.loading);    }}